package ir.ariyana.instagram.fragment

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.StaggeredGridLayoutManager
import ir.ariyana.instagram.adapter.AdapterExplore
import ir.ariyana.instagram.data.Explore
import ir.ariyana.instagram.databinding.FragmentExploreBinding

class FragmentExplore : Fragment() {

    lateinit var binding : FragmentExploreBinding
    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentExploreBinding.inflate(layoutInflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        val data : ArrayList<Explore> = arrayListOf(
            Explore("data:image/jpeg;base64,"),
            Explore("data:image/jpeg;base64,"),
            Explore("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTmpfMGCfU0frSa4xsp31m5irELqnlZ_U-5Jg&usqp=CAU"),
            Explore("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcROzGFgQ75O8rNkl1oJ9ezmQV1zn942U07w9g&usqp=CAU"),
            Explore("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ0NW8tncnF8OA5k5qUyHbGRkGF4xNr1ZoUbQ&usqp=CAU"),
            Explore("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQq7LCZM1A1WJJ0qIcSjbOja7P9k8gyQkgCiA&usqp=CAU"),
        )

        val adapter = AdapterExplore(data)
        binding.exploreRecyclerView.adapter = adapter
        binding.exploreRecyclerView.layoutManager = StaggeredGridLayoutManager(2, StaggeredGridLayoutManager.VERTICAL)
    }
}